import { describe, expect, it } from "vitest";
import { Keypair, Networks as StellarNetworks } from "@stellar/stellar-sdk";
import { createStellarSigner, isStellarSigner } from "./signer";
import { AssembledTransaction } from "@stellar/stellar-sdk/contract";

describe("Stellar Ed25519 Signer", () => {
  const validSecret = "SDV3OZOPGIO6GQAVI7T6ZJ7NSNFB26JX6QZYCI64TBC7BAZY6FQVAXXK";
  const validPublicKey = "GBBO4ZDDZTSM2IUKQYBAST3CFHNPFXECGEFTGWTA2WELR2BIWDK57UVE";

  describe("createStellarSigner", () => {
    it("should create a valid contract signer that can sign with signAuthEntry", async () => {
      const unsignedTxXDR =
        "";
      const expectedSignedTxXDR =
        "";

      const gotSigner = createStellarSigner(validSecret, "stellar-testnet");
      expect(gotSigner.address).toEqual(validPublicKey);

      // parse the unsigned tx XDR into AssembledTransaction
      const { method, tx, simulationResult, simulationTransactionData } = JSON.parse(
        Buffer.from(unsignedTxXDR, "base64").toString("utf8"),
      );
      const recoveredTx = AssembledTransaction.fromJSON(
        {
          contractId: "CBIELTK6YBZJU5UP2WWQEUCYKLPU6AUNZ2BQ4WWFEIE3USCIHMXQDAMA",
          networkPassphrase: StellarNetworks.TESTNET,
          rpcUrl: "https://soroban-testnet.stellar.org",
          method,
          parseResultXdr: result => result,
        },
        { tx, simulationResult, simulationTransactionData },
      );

      // ensure the tx is the same
      const recoveredTxXDR = Buffer.from(recoveredTx.toJSON()).toString("base64");
      expect(recoveredTxXDR).toBe(unsignedTxXDR);

      // ensure the Tx is missing the signer we have
      let missingSigners = recoveredTx.needsNonInvokerSigningBy();
      expect(missingSigners).toEqual([gotSigner.address]);

      // ensure the tx is signed successfully
      await recoveredTx.signAuthEntries({
        address: gotSigner.address,
        signAuthEntry: gotSigner.signAuthEntry,
        expiration: 2345678,
      });
      missingSigners = recoveredTx.needsNonInvokerSigningBy();
      expect(missingSigners).toHaveLength(0);

      // ensure the result of the signed tx is the same as the expected signed tx
      const signedTxXDR = Buffer.from(recoveredTx.toJSON()).toString("base64");
      expect(signedTxXDR).toBe(expectedSignedTxXDR);
    });

    it("should include signTransaction in the contract signer", () => {
      const signer = createStellarSigner(validSecret, "stellar-testnet");
      expect(signer.signTransaction).toBeDefined();
      expect(typeof signer.signTransaction).toBe("function");
    });

    it("should create different signers for different secret keys", () => {
      const secret1 = "SA6LFVPCYMDQILBRXQ2B2HRPK6DV2TX4FTQQQHWFPSCSY4H2RTCD3XAK";
      const secret2 = "SBFCBBFETW6U5HSOADUUXTQMUEXK7DIQLLATM6OVKKBQNG3I3EWIYJAW";

      const signer1 = createStellarSigner(secret1, "stellar-testnet");
      const signer2 = createStellarSigner(secret2, "stellar-testnet");

      expect(signer1).toBeDefined();
      expect(signer2).toBeDefined();
      expect(signer1.address).not.toBe(signer2.address);
    });

    it("should throw error for invalid secret key format", () => {
      const invalidSecret = "INVALID_SECRET_KEY";
      expect(() => createStellarSigner(invalidSecret, "stellar-testnet")).toThrow();
    });

    it("should throw error for empty string", () => {
      expect(() => createStellarSigner("", "stellar-testnet")).toThrow();
    });

    it("should throw error for public key instead of secret key", () => {
      expect(() => createStellarSigner(validPublicKey, "stellar-testnet")).toThrow();
    });
  });

  describe("isStellarSigner", () => {
    it("should return true for a valid contract signer", () => {
      const signer = createStellarSigner(validSecret, "stellar-testnet");
      expect(isStellarSigner(signer)).toBe(true);
    });

    it("should return true for object with address, signAuthEntry, and signTransaction", () => {
      const mockSigner = {
        address: "GBBO4ZDDZTSM2IUKQYBAST3CFHNPFXECGEFTGWTA2WELR2BIWDK57UVE",
        signAuthEntry: async () => ({ signedAuthEntry: "" }),
        signTransaction: async () => {},
      };
      expect(isStellarSigner(mockSigner)).toBe(true);
    });

    it("should return false for null, undefined, or primitives", () => {
      expect(isStellarSigner(null)).toBe(false);
      expect(isStellarSigner(undefined)).toBe(false);
      expect(isStellarSigner("string")).toBe(false);
      expect(isStellarSigner(123)).toBe(false);
      expect(isStellarSigner(true)).toBe(false);
    });
    it("should return false for object missing address", () => {
      const obj = { signAuthEntry: () => {} };
      expect(isStellarSigner(obj)).toBe(false);
    });

    it("should return false for object missing signAuthEntry", () => {
      const obj = { address: "GBBO4ZDDZTSM2IUKQYBAST3CFHNPFXECGEFTGWTA2WELR2BIWDK57UVE" };
      expect(isStellarSigner(obj)).toBe(false);
    });

    it("should return false for object with non-function signAuthEntry", () => {
      const obj = {
        address: "GBBO4ZDDZTSM2IUKQYBAST3CFHNPFXECGEFTGWTA2WELR2BIWDK57UVE",
        signAuthEntry: "not a function",
      };
      expect(isStellarSigner(obj)).toBe(false);
    });

    it("should return false for object with non-function signTransaction", () => {
      const obj = {
        address: "GBBO4ZDDZTSM2IUKQYBAST3CFHNPFXECGEFTGWTA2WELR2BIWDK57UVE",
        signAuthEntry: () => {},
        signTransaction: "not a function",
      };
      expect(isStellarSigner(obj)).toBe(false);
    });

    it("should return false for Keypair", () => {
      const keypair = Keypair.fromSecret(validSecret);
      expect(isStellarSigner(keypair)).toBe(false);
    });
  });
});
